import React from 'react'
import Card from '../UI/Card'
import styles from './User.module.css'
import Button from '../Button/Button'
import UserList from "./UserList";

function User() {
  const [username, setUsername] = React.useState('')
  const [age, setAge] = React.useState('')
  const [isValidUser, setIsValidUser] = React.useState(true)
  const [isValidAge, setIsValidAge] = React.useState(true)

  const[users, setUsers] = React.useState([]); 

  const addUser = (enteredUser) => {

    setUsers(enteredUser);

  }

  const userChangeHandler = (event) => {
    setUsername(event.target.value)
    if (username.trim().length > 0) {
      setIsValidUser(true)
    }
  }

  const ageChangeHandler = (event) => {
    setAge(event.target.value)
    if (age.trim().length > 0) {
      setIsValidAge(true)
    }
  }

  const submitFormHandler = (event) => {
    event.preventDefault()

    if (username.trim().length === 0) {
      setIsValidUser(false);
    }

    if (age.trim().length === 0) {
      setIsValidAge(false)
    }

    setUsername('');
    setAge('');

  }

  return (
    <Card className={styles.user}>
      <form onSubmit={submitFormHandler}>
        <div>
          <label>Username</label>
          <input type="text" onChange={userChangeHandler} value={username}/>
        </div>
        <div>
          <label>Age(Years)</label>
          <input type="number" onChange={ageChangeHandler} value={age}/>
        </div>
        <Button onSubmit={submitFormHandler} />
      </form>
      <UserList />
    </Card>
  )
}

export default User
