import React from 'react'
import Card from '../UI/Card'
import styles from './User.module.css'
import Button from '../Button/Button'

function User() {
  const [username, setUsername] = React.useState('');
  const [age, setAge] = React.useState('');
  const [isValidUser, setIsValidUser] = React.useState(false);
  const [isValidAge, setIsValidAge] = React.useState(false);


  const userChangeHandler = (event) => {
    setUsername(event.target.value)
    if (username.trim().length > 0) {
      setIsValid(true)
    }
  }

  const ageChangeHandler = (event) => {
    setAge(event.target.value)
    if (age.trim().length > 0) {
      setIsValid(true)
    }
  }

  const submitFormHandler = (event) => {
    event.preventDefault()

    if (!isValidUser || !isValidAge ) {
      setIsValid(false);
      return
    }
  }

  return (
    <Card className={styles.user}>
      <form onSubmit={submitFormHandler}>
        <div>
          <label>Username</label>
          <input
            type="text"
            onChange={userChangeHandler}
            className={`${!isValid && styles.invalid}`}
          />
        </div>
        <div>
          <label>Age(Years)</label>
          <input type="text" onChange={ageChangeHandler} />
        </div>
        <Button onSubmit={submitFormHandler} />
      </form>
    </Card>
  )
}

export default User
