import React from 'react'
import Card from '../UI/Card'
import styles from './User.module.css'
import Button from '../Button/Button'

function User() {
  const [username, setUsername] = React.useState('');
  const [age, setAge] = React.useState('');
  const [isValidUser, setIsValidUser] = React.useState(true);
  const [isValidAge, setIsValidAge] = React.useState(true);


  const userChangeHandler = (event) => {
    setUsername(event.target.value);
    if (username.trim().length > 0) {
      setIsValidUser(true);
    }
  }

  const ageChangeHandler = (event) => {
    setAge(event.target.value);
    if (age.trim().length > 0) {
      setIsValidAge(true);
    }
  }

  const submitFormHandler = (event) => {
    event.preventDefault();

    if (username.trim().length === 0) {
      setIsValidUser(false);
      <InputModel />
    }
    if(age.trim().length === 0){
        setIsValidAge(false);
        
    }
  }

  return (
    <Card className={styles.user}>
      <form onSubmit={submitFormHandler}>
        <div className={`${!isValidUser && styles.invalid}`}>
          <label>Username</label>
          <input
            type="text"
            onChange={userChangeHandler}
            
          />
        </div>
        <div className={`${!isValidAge && styles.invalid}`}> 
          <label>Age(Years)</label>
          <input type="text" onChange={ageChangeHandler}  />
        </div>
        <Button onSubmit={submitFormHandler} />
      </form>
    </Card>
  )
}

export default User
